#fonction min
def indice_min(liste):
    mini=liste[0]
    indice_min=0
    for i in range(len(liste)):
        if liste[i]<=mini:
            mini=liste[i]
            indice_min=i
    return indice_min


#fonction max
def indice_max(liste):
    maxi=liste[0]
    indice_max=0
    for i in range(len(liste)):
        if liste[i]>=maxi:
            maxi=liste[i]
            indice_max=i
    return indice_max


